import React from 'react';
import { Link } from 'react-router-dom';

import './Homepage.css';
import imageJIRA from '../Image/jira.png'

function Option() {
    //Create image and option list
    const optionList = ['Create JIRA Story','Host Daily Standup','Host Retro']
    const pageRoute = ['/jira', '/DSU', '/Retro']


    const handleClick = (item) => {
        console.log("Click the ", item)
    }

    //Rendered the homepage list item
    const renderedItmes = optionList.map((item, index) => (
        <div className="optionBox" key={index}>
            <div>
                <img className="homepageImage" src={imageJIRA} alt="JIRA"/>
            </div>
            <div className='buttonBox'>
                <div className='optionButton' onClick={() => handleClick(item)}>
                        <Link to="/jira">{item}</Link>
                </div>  
            </div>
        </div>
    ));

    return(
        <div className="optionsContainer">
            {renderedItmes}
        </div>
    );
}

export default function Homepage() {
    return (
        <div className="homepageComponents">
            <div>
                <h1 className='centered-text'>Welcome to the Pro-Scrum World</h1>
                <Option />
            </div>
        </div>
    );
}
